unsigned char get_n_to_k_table(unsigned char n, unsigned char p){
	if (n > 30){
		printk("invalid n: %d \n", n);
		return 0;
	}
	
	else if (p > 100){
		printk("invalid n: %d \n", p);
		return 0;
	} 
	return ntok_table[n-1][p];
} 

unsigned char get_k_to_n_table(unsigned char k, unsigned char p){
	if (k > 10){
		printk("invalid n: %d \n", k);
		return 0;
	}
	
	else if (p > 100){
		printk("invalid n: %d \n", p);
		return 0;
	} 
	return kton_table[k-1][p];
}


unsigned int get_tot_rcv(struct tr_info* info){
	unsigned int ret = 0;
	unsigned int i=0;
	
	for (i=0; i<NUM_MCS; i++){
		ret += info->rcv_num[i];
	}
	return ret;
}

unsigned char set_batt(unsigned char status, unsigned char capacity){
	return (status <<7)|capacity;
}

unsigned char get_capa(unsigned char batt){
	return batt & 0x7f;
}

unsigned char get_charge(unsigned char batt){
	unsigned char ret = 0;
	ret = (batt & (1<<7))>>7;

	if (ret <= 1)
		return ret;
	else
		return 0;
}
